var Promise = require('lie');
var readtools = require('lib/readtools');
var c = require('chalk');
// ----------------------------------------------------------------------------

module.exports = function (cwd, args, draftdir) {
	var type = 'production';
	if (args.length > 0) {
		type = args.shift();
	}

	var category = 'production';
	if (args.length > 0) {
		category = args.shift();
	}

	var tools = readtools(cwd, '.valkyrja');
	var buildstep = tools.build(type, category, null);

	if (buildstep === true) {
		buildstep = Promise.resolve();
	}

	buildstep.then(function () {
		console.log('');
		console.log(c.blue('build succesful'))
		console.log('');
	}, function (reason) {
		if (reason) {
			if (reason.stack) {
				console.log(reason.stack);
			}
			else { // non-stack error
				console.log(reason);
			}
		}

		console.log('');
		console.log(c.red.dim('build failed'))
		console.log('');

		return Promise.reject();
	});
};
