var Promise = require('lie');
var readtools = require('lib/readtools');
var readconf = require('lib/readconf');
var c = require('chalk');
// ----------------------------------------------------------------------------

module.exports = function (cwd, args, draftdir) {
	var conf = readconf(cwd, '.valkyrja');

	var type = 'production';
	if (args.length > 0) {
		type = args.shift();
	}

	var category = 'production';
	if (args.length > 0) {
		category = args.shift();
	}

	var tools = readtools(cwd, '.valkyrja');
	var buildstep = tools.build(cwd, args, conf, type, category, null);

	if (buildstep === true) {
		buildstep = Promise.resolve();
	}

	buildstep.then(function () {
		console.log('');
		console.log(c.blue('build succesful'))
		console.log('');
	}, function (reason) {
		if ( ! /exited with code/.test('' + reason)) {
			console.log(reason.stack || reason);
			console.log('');
		}

		console.log(c.red.dim('build failed'))
		console.log('');

		return Promise.reject();
	});
};
