var Promise = require('lie');
var file_exists = require('is-there');
var _ = {
	merge: require('lodash.merge'),
};
// ----------------------------------------------------------------------------

module.exports = function readconf(cwd, basename) {
	var toolsfile = cwd + '/' + basename + '.js';

	if (file_exists(toolsfile)) {
		tools = require(toolsfile)
	}
	else { // no tools file
		tools = {};
	}

	return _.merge({}, {

		build: function (type, category, host) {
			return Promise.resolve();
		},

		check: function (type, category, host) {
			return Promise.resolve();
		},

		clean: function (cwd) {
			return Promise.resolve();
		},

		confdryrun: function (conf, rsync, ssh, host) {
			// extra settings
		},

		confdeploy: function (conf, rsync, ssh, host) {
			// extra settings
		},

		confdiff: function (conf, rsync, ssh, host) {
			// extra settings
		}

	}, tools);
}
